sqlMap.select:
/* MdmMcmOfficeSQL.xml -- getOfcList */ SELECT a.ofc_eng_nm, a.ofc_cd, a.prnt_ofc_cd, a.ofc_level FROM (SELECT ofc_eng_nm, ofc_cd, prnt_ofc_cd, dbo.fc_ofc_compare(ofc_cd,#ofc_cd#) ofc_level FROM tb_ofc WHERE delt_flg = 'N' ) a WHERE a.ofc_level in('D','U','E')
AND OFC_CD LIKE ISNULL((SELECT CASE ISNULL(MAX(A.USE_FLG), 'N') WHEN 'Y' THEN '' ELSE MAX(B.OFC_CD) END FROM TB_ROLE_FNC A, TB_USR B WHERE A.ROLE_CD = B.ROLE_CD AND ROLE_FNC_CD = 'O01' AND B.USRID = #usrid# ), '') + '%'
ORDER BY dbo.fc_ofc_rank(ofc_cd)

