/** RptPrintSQL.xml > searchMblSummaryMultiProfitList **/
SELECT #inv_curr_cd# as inv_curr_cd ,prf.bl_kind ,prf.bl_no ,prf.sell_buy_tp_cd ,case when prf.inv_no != '' then prf.inv_no else '(None)' end AS inv_no ,prf.bill_to ,prf.inv_post_dt ,sum(prf.local_inv_amt) AS local_inv_ttl_amt ,sum(prf.cost_inv_amt) AS cost_inv_ttl_amt ,sum(prf.agent_inv_amt) AS agent_inv_ttl_amt ,sum(prf.local_inv_amt - prf.cost_inv_amt + prf.agent_inv_amt) AS prf_amt ,(SELECT MIN(j_mst.clr_yn) FROM tb_jnr_mst j_mst, tb_jnr_dtl j_dtl WHERE j_mst.jnr_no = j_dtl.jnr_no AND j_dtl.inv_seq = prf.inv_seq AND ISNULL(j_mst.DELT_FLG,'N') = 'N' AND ISNULL(j_dtl.DELT_FLG,'N') = 'N' AND ISNULL(j_mst.VOID_YN,'N') = 'N') AS clr_yn ,prf.org_bl_rcvd_flg ,prf.rcvd_dt_tm ,prf.express_tp_cd ,prf.rlsd_dt_tm ,prf.last_chk_no ,prf.last_pay_dt
,max(prf.ref_ofc_cd) AS ref_ofc_cd ,prf.inv_seq ,max(prf.trdp_cd) AS trdp_cd ,max(prf.ofc_cd) AS ofc_cd ,(select upper(x.CNT_CD) from tb_ofc x where x.OFC_CD = MAX(prf.ofc_cd) ) AS bl_cnt_cd FROM (SELECT bl.bl_kind ,bl.bl_no ,inv.sell_buy_tp_cd ,CASE WHEN inv.sell_buy_tp_cd = 'B' THEN Isnull(dtl.buy_inv_no, '(None)') ELSE Isnull(dtl.inv_no, '(None)') END AS inv_no ,(SELECT trdp.locl_nm FROM tb_trdp trdp WHERE trdp.trdp_cd = inv.trdp_cd) AS bill_to ,dtl.inv_post_dt ,CASE WHEN inv.sell_buy_tp_cd = 'S' THEN Isnull(frt.inv_amt, 0) ELSE 0 END AS local_inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'B' THEN Isnull(frt.inv_amt, 0) ELSE 0 END AS cost_inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'C' OR inv.sell_buy_tp_cd = 'D' THEN Isnull(frt.inv_amt, 0) - ISNULL(frt.cr_amt, 0) ELSE 0 END AS agent_inv_amt ,(SELECT MAX(jnr_no) AS jnr_no FROM tb_jnr_dtl j_dtl WHERE j_dtl.inv_seq = dtl.inv_seq ) AS jnr_no ,bl.org_bl_rcvd_flg ,bl.rcvd_dt_tm ,bl.express_tp_cd ,bl.rlsd_dt_tm ,dtl.last_chk_no ,dtl.last_pay_dt ,bl.rgst_ofc_cd ,bl.ref_ofc_cd ,dtl.inv_seq ,inv.trdp_cd ,inv.ofc_cd FROM tb_frt frt JOIN (SELECT bl.bl_kind ,bl.bl_no ,bl.intg_bl_seq ,bl.rgst_ofc_cd ,bl.org_bl_rcvd_flg ,bl.rcvd_dt_tm ,bl.express_tp_cd ,bl.rlsd_dt_tm ,bl.ref_ofc_cd FROM (SELECT 'H' bl_kind ,hbl.bl_no ,hbl.intg_bl_seq ,hbl.rgst_ofc_cd ,hbl.org_bl_rcvd_flg ,hbl.rcvd_dt_tm ,hbl.express_tp_cd ,hbl.rlsd_dt_tm ,hbl.ref_ofc_cd FROM tb_intg_bl bl LEFT OUTER JOIN tb_intg_bl_rlt rlt ON bl.intg_bl_seq = rlt.rlt_intg_bl_seq AND rlt.delt_flg = 'N' LEFT OUTER JOIN tb_intg_bl hbl ON hbl.intg_bl_seq = rlt.intg_bl_seq AND hbl.delt_flg = 'N' WHERE bl.intg_bl_seq = #intg_bl_seq# UNION SELECT 'M' bl_kind ,bl.bl_no ,bl.intg_bl_seq AS intg_bl_seq ,bl.rgst_ofc_cd ,bl.org_bl_rcvd_flg ,bl.rcvd_dt_tm ,bl.express_tp_cd ,bl.rlsd_dt_tm ,bl.ref_ofc_cd FROM tb_intg_bl bl WHERE bl.intg_bl_seq = #intg_bl_seq#) bl) bl ON frt.intg_bl_seq = bl.intg_bl_seq JOIN tb_inv inv ON inv.inv_seq = frt.inv_seq AND inv.delt_flg = 'N' LEFT OUTER JOIN tb_inv_dtl dtl ON dtl.inv_seq = inv.inv_seq AND dtl.delt_flg = 'N' LEFT OUTER JOIN tb_frt_cd frt_cd ON frt_cd.frt_cd = frt.frt_cd AND frt_cd.delt_flg = 'N' WHERE 1=1 AND frt.inv_curr_cd = #inv_curr_cd# AND frt.delt_flg = 'N' AND frt_cd.pfmc_flg = 'Y' ) prf GROUP BY prf.bl_kind ,prf.inv_no ,prf.bl_no ,prf.sell_buy_tp_cd ,prf.bill_to ,prf.inv_post_dt ,prf.jnr_no ,prf.org_bl_rcvd_flg ,prf.rcvd_dt_tm ,prf.express_tp_cd ,prf.rlsd_dt_tm ,prf.last_chk_no ,prf.last_pay_dt ,prf.inv_seq ORDER BY prf.bl_kind DESC ,prf.bl_no ,prf.inv_no ,prf.bill_to ,prf.inv_post_dt

/** RptPrintSQL.xml > searchHblByHblMultiProfitList **/ SELECT #inv_curr_cd# as inv_curr_cd ,prf.bl_kind ,prf.frt_kind ,prf.frt_nm ,(prf.local_inv_amt * prf.ratio/100) AS local_inv_amt ,(prf.cost_inv_amt* prf.ratio/100) AS cost_inv_amt ,(prf.debit_inv_amt* prf.ratio/100) - (prf.cr_amt * prf.ratio/100) AS dc_inv_amt ,(prf.debit_inv_amt* prf.ratio/100) AS debit_inv_amt ,(prf.cr_amt * prf.ratio/100) AS cr_amt ,prf.bl_no ,prf.sell_buy_tp_cd ,prf.inv_no ,prf.inv_no2 ,prf.bill_to ,prf.inv_post_dt ,(SELECT MIN(j_mst.clr_yn) FROM tb_jnr_mst j_mst, tb_jnr_dtl j_dtl WHERE j_mst.jnr_no = j_dtl.jnr_no AND j_dtl.inv_seq = prf.inv_seq AND ISNULL(j_mst.DELT_FLG,'N') = 'N' AND ISNULL(j_dtl.DELT_FLG,'N') = 'N' AND ISNULL(j_mst.VOID_YN,'N') = 'N') AS clr_yn ,prf.ratio ,prf.inv_seq ,prf.ref_ofc_cd ,prf.trdp_cd ,prf.ofc_cd ,(select upper(x.CNT_CD) from tb_ofc x where x.OFC_CD = prf.ofc_cd ) AS bl_cnt_cd FROM (
SELECT bl.bl_kind ,CASE WHEN frt.intg_bl_seq <> ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) AND bl.sub_mbl_flg = 'N' THEN 'H' WHEN frt.intg_bl_seq <> ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) AND bl.sub_mbl_flg <> 'N' THEN 'S' ELSE bl.bl_kind END AS frt_kind ,((select frt_cd.frt_cd from tb_frt_cd frt_cd where frt_cd = frt.frt_cd) + ' / ' + frt.frt_cd_nm) AS frt_nm ,frt.inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'S' THEN frt.inv_amt ELSE 0 END AS local_inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'B' THEN frt.inv_amt ELSE 0 END AS cost_inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'C' or inv.sell_buy_tp_cd = 'D' THEN frt.inv_amt ELSE 0 END AS debit_inv_amt ,frt.cr_amt ,bl.bl_no ,inv.sell_buy_tp_cd ,dtl.inv_no ,dtl.inv_seq ,bl.ref_ofc_cd ,inv.trdp_cd ,inv.ofc_cd ,CASE WHEN inv.sell_buy_tp_cd = 'B' THEN CASE WHEN ISNULL(dtl.buy_inv_no, '') = '' then '(None)' else dtl.buy_inv_no end ELSE ISNULL(dtl.inv_no , '(None)') END AS inv_no2 ,(SELECT trdp.locl_nm FROM tb_trdp trdp WHERE trdp.trdp_cd = inv.trdp_cd) AS bill_to ,dtl.inv_post_dt ,(SELECT MAX(jnr_no) AS jnr_no FROM tb_jnr_dtl j_dtl WHERE j_dtl.inv_seq = dtl.inv_seq ) AS jnr_no ,CASE WHEN frt.intg_bl_seq <> ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) AND sub_mbl_flg = 'N' THEN 100 ELSE bl.ratio END AS ratio
FROM tb_frt frt JOIN (SELECT 'H' bl_kind, hbl.bl_no, hbl.intg_bl_seq, hbl.rgst_ofc_cd, hbl.org_bl_rcvd_flg, hbl.rcvd_dt_tm, hbl.express_tp_cd, hbl.rlsd_dt_tm, isnull(hbl.sub_mbl_flg, 'N') AS sub_mbl_flg, 100 ratio, hbl.ref_ofc_cd FROM tb_intg_bl hbl WHERE hbl.intg_bl_seq = #intg_bl_seq# UNION SELECT 'M' bl_kind, mbl.bl_no, mbl.intg_bl_seq, mbl.rgst_ofc_cd, mbl.org_bl_rcvd_flg, mbl.rcvd_dt_tm, mbl.express_tp_cd, mbl.rlsd_dt_tm, isnull(hbl.sub_mbl_flg, 'N') as sub_mbl_flg, CASE WHEN #air_sea_clss_cd# = 'S' THEN CASE WHEN isnull(CONVERT(FLOAT, #mbl_volume#), 0) = 0 THEN 0 ELSE (CASE WHEN (hbl.meas - hbl.grs_wgt/1000) > 0 THEN hbl.meas ELSE hbl.grs_wgt/1000 END)/CONVERT(FLOAT, #mbl_volume#)*100 END WHEN #air_sea_clss_cd# = 'A' THEN CASE WHEN ISNULL(CONVERT(FLOAT, #mbl_volume#), 0) = 0 THEN 0 ELSE hbl.grs_wgt/CONVERT(FLOAT, #mbl_volume#)*100/1000*1000 END END as ratio , mbl.ref_ofc_cd FROM tb_intg_bl hbl LEFT OUTER JOIN tb_intg_bl_rlt rlt ON rlt.intg_bl_seq = hbl.intg_bl_seq AND hbl.delt_flg = 'N' LEFT OUTER JOIN tb_intg_bl mbl ON rlt.RLT_intg_bl_seq = mbl.intg_bl_seq AND mbl.delt_flg = 'N' WHERE hbl.intg_bl_seq = #intg_bl_seq# UNION SELECT 'M' bl_kind, mbl.bl_no, sbl.intg_bl_seq, mbl.rgst_ofc_cd, mbl.org_bl_rcvd_flg, mbl.rcvd_dt_tm, mbl.express_tp_cd, mbl.rlsd_dt_tm, ISNULL(sbl.sub_mbl_flg, 'N') as sub_mbl_flg, CASE WHEN #air_sea_clss_cd# = 'S' THEN CASE WHEN ISNULL(CONVERT(FLOAT, #mbl_volume#), 0) = 0 THEN 0 ELSE (CASE WHEN (hbl.meas - hbl.grs_wgt/1000) > 0 THEN hbl.meas ELSE hbl.grs_wgt/1000 END)/CONVERT(FLOAT, #mbl_volume#)*100 END END AS ratio , mbl.ref_ofc_cd FROM tb_intg_bl hbl LEFT OUTER JOIN TB_INTG_BL_RLT rlt ON rlt.INTG_BL_SEQ = hbl.intg_bl_seq AND rlt.delt_flg = 'N' LEFT OUTER JOIN TB_INTG_BL mbl ON rlt.RLT_INTG_BL_SEQ = mbl.INTG_BL_SEQ AND mbl.delt_flg = 'N' LEFT OUTER JOIN TB_INTG_BL_RLT srlt ON srlt.RLT_INTG_BL_SEQ = mbl.intg_bl_seq AND srlt.delt_flg = 'N' LEFT OUTER JOIN TB_INTG_BL sbl ON srlt.INTG_BL_SEQ = sbl.INTG_BL_SEQ AND mbl.delt_flg = 'N' WHERE hbl.intg_bl_seq = #intg_bl_seq# AND ISNULL(sbl.sub_mbl_flg, 'N') = 'Y' ) bl ON bl.intg_bl_seq = ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) JOIN tb_inv inv ON inv.inv_seq = frt.inv_seq and inv.delt_flg = 'N' LEFT OUTER JOIN tb_inv_dtl dtl ON dtl.inv_seq = inv.inv_seq and dtl.delt_flg = 'N' LEFT OUTER JOIN tb_frt_cd frt_cd ON frt_cd.frt_cd = frt.frt_cd and frt_cd.delt_flg = 'N' WHERE 1=1 AND frt.inv_curr_cd = #inv_curr_cd# AND frt.delt_flg = 'N' AND frt_cd.pfmc_flg = 'Y' ) prf ORDER BY prf.bl_kind DESC ,prf.inv_no ,prf.frt_kind DESC ,prf.bl_no

/** RptPrintSQL.xml > searchHblByHblOneProfitList **/ SELECT prf.inv_curr_cd ,prf.bl_kind ,prf.frt_kind ,prf.frt_nm ,prf.xch_rt_ut as xch_rt_ut ,(prf.local_inv_amt * prf.ratio / 100) AS local_inv_amt ,(prf.cost_inv_amt * prf.ratio / 100) AS cost_inv_amt ,(prf.debit_inv_amt * prf.ratio / 100) - (prf.cr_amt * prf.ratio / 100) AS dc_inv_amt ,(prf.debit_inv_amt * prf.ratio / 100) AS debit_inv_amt ,(prf.cr_amt * ratio / 100) AS cr_amt ,prf.bl_no ,prf.sell_buy_tp_cd ,prf.inv_no ,prf.inv_no2 ,prf.bill_to ,prf.inv_post_dt ,(SELECT MIN(j_mst.clr_yn) FROM tb_jnr_mst j_mst, tb_jnr_dtl j_dtl WHERE j_mst.jnr_no = j_dtl.jnr_no AND j_dtl.inv_seq = prf.inv_seq AND ISNULL(j_mst.DELT_FLG,'N') = 'N' AND ISNULL(j_dtl.DELT_FLG,'N') = 'N' AND ISNULL(j_mst.VOID_YN,'N') = 'N') AS clr_yn ,prf.ratio ,prf.inv_seq ,prf.ref_ofc_cd ,prf.trdp_cd ,prf.ofc_cd ,(select upper(x.CNT_CD) from tb_ofc x where x.OFC_CD = prf.ofc_cd ) AS bl_cnt_cd FROM (
SELECT bl.bl_kind ,CASE WHEN frt.intg_bl_seq <> ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) AND bl.sub_mbl_flg = 'N' THEN 'H' WHEN frt.intg_bl_seq <> ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) AND bl.sub_mbl_flg <> 'N' THEN 'S' ELSE bl.bl_kind END AS frt_kind ,((select frt_cd.frt_cd from tb_frt_cd frt_cd where frt_cd = frt.frt_cd) + ' / ' + frt.frt_cd_nm) AS frt_nm ,frt.inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'S' THEN ISNULL(frt.inv_amt, 0) * (CASE WHEN frt.inv_curr_cd = #f_curr_cd# THEN 1 ELSE ISNULL(dd_ex_rt.xch_rt_ut, ISNULL(mm_ex_rt.xch_rt_ut, ISNULL(gd_ex_rt.xch_rt_ut, 0))) END) ELSE 0 END AS local_inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'B' THEN ISNULL(frt.inv_amt, 0) * (CASE WHEN frt.inv_curr_cd = #f_curr_cd# THEN 1 ELSE ISNULL(dd_ex_rt.xch_rt_ut, ISNULL(mm_ex_rt.xch_rt_ut, ISNULL(gd_ex_rt.xch_rt_ut, 0))) END) ELSE 0 END AS cost_inv_amt ,CASE WHEN inv.sell_buy_tp_cd = 'C' OR inv.sell_buy_tp_cd = 'D' THEN ISNULL(frt.inv_amt, 0) * (CASE WHEN frt.inv_curr_cd = #f_curr_cd# THEN 1 ELSE ISNULL(dd_ex_rt.xch_rt_ut, ISNULL(mm_ex_rt.xch_rt_ut, ISNULL(gd_ex_rt.xch_rt_ut, 0))) END) ELSE 0 END AS debit_inv_amt ,ISNULL(frt.cr_amt, 0) *(CASE WHEN frt.inv_curr_cd = #f_curr_cd# THEN 1 ELSE ISNULL(dd_ex_rt.xch_rt_ut, ISNULL(mm_ex_rt.xch_rt_ut, ISNULL(gd_ex_rt.xch_rt_ut, 0))) END) as cr_amt ,bl.bl_no ,inv.sell_buy_tp_cd ,dtl.inv_no ,dtl.inv_seq ,bl.ref_ofc_cd ,inv.trdp_cd ,inv.ofc_cd ,CASE WHEN inv.sell_buy_tp_cd = 'B' THEN CASE WHEN ISNULL(dtl.buy_inv_no, '') = '' THEN '(None)' ELSE dtl.buy_inv_no END ELSE ISNULL(dtl.inv_no , '(None)') END AS inv_no2 ,(SELECT trdp.locl_nm FROM tb_trdp trdp WHERE trdp.trdp_cd = inv.trdp_cd) AS bill_to ,dtl.inv_post_dt ,(SELECT MAX(jnr_no) AS jnr_no FROM tb_jnr_dtl j_dtl WHERE j_dtl.inv_seq = dtl.inv_seq ) AS jnr_no ,CASE WHEN frt.intg_bl_seq <> ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) AND sub_mbl_flg = 'N' THEN 100 ELSE bl.ratio END AS ratio ,(CASE WHEN frt.inv_curr_cd = #f_curr_cd# THEN 1 ELSE ISNULL(dd_ex_rt.xch_rt_ut, ISNULL(mm_ex_rt.xch_rt_ut, ISNULL(gd_ex_rt.xch_rt_ut, 0))) END) as xch_rt_ut ,#f_curr_cd# as inv_curr_cd
FROM tb_frt frt JOIN (SELECT 'H' bl_kind, hbl.bl_no, hbl.intg_bl_seq, hbl.rgst_ofc_cd, hbl.org_bl_rcvd_flg, hbl.rcvd_dt_tm, hbl.express_tp_cd, hbl.rlsd_dt_tm, ISNULL(hbl.sub_mbl_flg, 'N') AS sub_mbl_flg, 100 ratio, hbl.ref_ofc_cd FROM tb_intg_bl hbl WHERE hbl.intg_bl_seq = #intg_bl_seq# UNION SELECT 'M' bl_kind, mbl.bl_no, mbl.intg_bl_seq, mbl.rgst_ofc_cd, mbl.org_bl_rcvd_flg, mbl.rcvd_dt_tm, mbl.express_tp_cd, mbl.rlsd_dt_tm, ISNULL(hbl.sub_mbl_flg, 'N') AS sub_mbl_flg, CASE WHEN #air_sea_clss_cd# = 'S' THEN CASE WHEN isnull(CONVERT(FLOAT, #mbl_volume#), 0) = 0 THEN 0 ELSE (CASE WHEN (hbl.meas - hbl.grs_wgt/1000) > 0 THEN hbl.meas ELSE hbl.grs_wgt/1000 END)/CONVERT(FLOAT, #mbl_volume#)*100 END WHEN #air_sea_clss_cd# = 'A' THEN CASE WHEN isnull(CONVERT(FLOAT, #mbl_volume#), 0) = 0 THEN 0 ELSE hbl.grs_wgt/CONVERT(FLOAT, #mbl_volume#)*100/1000*1000 END END AS ratio , mbl.ref_ofc_cd FROM tb_intg_bl hbl LEFT OUTER JOIN tb_intg_bl_rlt rlt ON rlt.INTG_BL_SEQ = hbl.intg_bl_seq AND rlt.delt_flg = 'N' LEFT OUTER JOIN tb_intg_bl mbl ON rlt.rlt_intg_bl_seq = mbl.intg_bl_seq AND mbl.delt_flg = 'N' WHERE hbl.intg_bl_seq = #intg_bl_seq# UNION SELECT 'M' bl_kind, mbl.bl_no, sbl.intg_bl_seq, mbl.rgst_ofc_cd, mbl.org_bl_rcvd_flg, mbl.rcvd_dt_tm, mbl.express_tp_cd, mbl.rlsd_dt_tm, ISNULL(sbl.sub_mbl_flg, 'N') as sub_mbl_flg, CASE WHEN #air_sea_clss_cd# = 'S' THEN CASE WHEN isnull(CONVERT(FLOAT, #mbl_volume#), 0) = 0 THEN 0 ELSE (CASE WHEN (hbl.meas - hbl.grs_wgt/1000) > 0 THEN hbl.meas ELSE hbl.grs_wgt/1000 END)/CONVERT(FLOAT, #mbl_volume#)*100 END END AS ratio , mbl.ref_ofc_cd FROM tb_intg_bl hbl LEFT OUTER JOIN tb_intg_bl_rlt rlt on rlt.intg_bl_seq = hbl.intg_bl_seq and rlt.delt_flg = 'N' LEFT OUTER JOIN tb_intg_bl mbl on rlt.rlt_intg_bl_seq = mbl.intg_bl_seq and mbl.delt_flg = 'N' LEFT OUTER JOIN tb_intg_bl_rlt srlt on srlt.rlt_intg_bl_seq = mbl.intg_bl_seq and srlt.delt_flg = 'N' LEFT OUTER JOIN tb_intg_bl sbl on srlt.intg_bl_seq = sbl.intg_bl_seq and mbl.delt_flg = 'N' WHERE hbl.intg_bl_seq = #intg_bl_seq# AND ISNULL(sbl.sub_mbl_flg, 'N') = 'Y' ) bl ON bl.intg_bl_seq = ISNULL(frt.intg_bl_seq_2, frt.intg_bl_seq) JOIN tb_inv inv ON inv.inv_seq = frt.inv_seq and inv.delt_flg = 'N' LEFT OUTER JOIN tb_inv_dtl dtl ON dtl.inv_seq = inv.inv_seq and dtl.delt_flg = 'N' LEFT OUTER JOIN tb_frt_cd frt_cd ON frt_cd.frt_cd = frt.frt_cd and frt_cd.delt_flg = 'N' LEFT OUTER JOIN tb_finc_perf_xcrt dd_ex_rt ON dd_ex_rt.fm_curr_cd = frt.inv_curr_cd AND dd_ex_rt.delt_flg = 'N' AND dd_ex_rt.to_curr_cd = #f_curr_cd# AND dd_ex_rt.dt_clss_cd = 'D' AND dd_ex_rt.aply_fm_dt = frt.inv_post_dt LEFT OUTER JOIN tb_finc_perf_xcrt mm_ex_rt ON mm_ex_rt.fm_curr_cd = frt.inv_curr_cd AND mm_ex_rt.delt_flg = 'N' AND mm_ex_rt.to_curr_cd = #f_curr_cd# AND mm_ex_rt.dt_clss_cd = 'M' AND substring(mm_ex_rt.aply_fm_dt,1,6) = substring(frt.inv_post_dt,1,6) LEFT OUTER JOIN ($one_curr_rate_sql$) gd_ex_rt ON gd_ex_rt.curr_cd = frt.inv_curr_cd AND gd_ex_rt.aply_fm_dt = substring(frt.inv_post_dt,1,6) WHERE frt.delt_flg = 'N' AND frt_cd.pfmc_flg = 'Y' ) prf ORDER BY prf.bl_kind DESC ,prf.inv_no ,prf.frt_kind DESC ,prf.bl_no
