sqlMap.select:
/* WHPutaway.searchWHPutawayInfo */ SELECT R.WIB_IN_NO ,R.WIB_BK_NO ,R.WH_CD ,CONVERT(VARCHAR(10), R.INBOUND_DT, 110) AS INBOUND_DT ,R.INBOUND_HM ,P.SUPV_NM ,CASE ISNULL(P.PUTAWAY_HM_FR, 'NULL') WHEN 'NULL' THEN P.PUTAWAY_HM_FR ELSE SUBSTRING(P.PUTAWAY_HM_FR, 0, 3) + ':' + SUBSTRING(P.PUTAWAY_HM_FR, 3, LEN(P.PUTAWAY_HM_FR)) END AS PUTAWAY_HM_FR ,CASE ISNULL(P.PUTAWAY_HM_TO, 'NULL') WHEN 'NULL' THEN P.PUTAWAY_HM_TO ELSE SUBSTRING(P.PUTAWAY_HM_TO, 0, 3) + ':' + SUBSTRING(P.PUTAWAY_HM_TO, 3, LEN(P.PUTAWAY_HM_TO)) END AS PUTAWAY_HM_TO ,P.WORK_NM ,P.MSG_TO_WK ,CASE ISNULL (P.WIB_IN_NO, '') WHEN '' THEN 'NEW' ELSE 'UPDATE' END AS FORM_MODE FROM TL_WH_RCV R LEFT JOIN TL_WH_PUTAWAY P ON R.WIB_IN_NO = P.WIB_IN_NO WHERE 1=1
AND R.WIB_IN_NO = #c_wib_in_no#
AND R.WIB_BK_NO = #c_wib_bk_no#
