sqlMap.select:
/* WhmReceivingSQL > searchReceivingRSQL */ SELECT RS.FILE_NO, RS.RGST_OFC_CD, RS.WH_CD, WH.WH_NM, RS.RCV_SHP_TP_CD, RS.CTRT_NO, (SELECT TL_CTRT.CTRT_NM CTRT_NM FROM TL_CTRT WHERE 1=1 AND TL_CTRT.CTRT_NO = RS.CTRT_NO
) AS CTRT_NM, RS.CUST_CD, (SELECT top 1 TP.LOCL_NM FROM TB_TRDP TP WHERE TP.TRDP_CD = RS.CUST_CD
) AS CUST_NM, RS.SPLR_RCVR_CD, (SELECT top 1 TP.LOCL_NM FROM TB_TRDP TP WHERE TP.TRDP_CD = RS.SPLR_RCVR_CD
) AS SPLR_RCVR_NM, RS.TRKR_CD, (SELECT top 1 TP.LOCL_NM FROM TB_TRDP TP WHERE TP.TRDP_CD = RS.TRKR_CD
) AS TRKR_NM, RS.CUST_REF_NO, RS.INTER_RMK, RS.XTER_RMK, RS.RCV_SHP_FLG, RS.OPR_CD, (SELECT TOP 1 US.ENG_USR_NM FROM TB_USR US WHERE US.USRID = RS.OPR_CD
) AS OPR_NM, RS.ESTM_RCV_DT, RS.RCV_SHP_DT, ISNULL(RS.PLT_NO, 0) PLT_NO, RS.CNTR_NO, RS.MST_BL_NO, RS.HUS_BL_NO, CUST_OFC_CD, SPLR_RCVR_OFC_CD, TRKR_OFC_CD, OPR_OFC_CD, LEN_UT_CD, CONVERT(VARCHAR, RS.CRE_DT, 110) + ' ' + CONVERT(VARCHAR(5), RS.CRE_DT, 8) AS CRE_DT FROM WHM_WH WH, WHM_RCV_SHP RS WHERE 1 =1 AND WH.WH_CD = RS.WH_CD
AND RS.FILE_NO = #file_no# AND RS.RGST_OFC_CD = #rgst_ofc_cd# AND RS.RCV_SHP_TP_CD = #rcv_shp_tp_cd#

sqlMap.insert:
/* WhmReceivingSQL > createReceivingCSQL */ INSERT INTO WHM_RCV_SHP ( FILE_NO, RGST_OFC_CD, WH_CD, RCV_SHP_TP_CD, CUST_CD, SPLR_RCVR_CD, TRKR_CD, CUST_REF_NO, INTER_RMK, XTER_RMK, RCV_SHP_FLG, OPR_CD, ESTM_RCV_DT, RCV_SHP_DT, PLT_NO, CNTR_NO, MST_BL_NO, HUS_BL_NO, CUST_OFC_CD, SPLR_RCVR_OFC_CD, TRKR_OFC_CD, OPR_OFC_CD, LEN_UT_CD,
CTRT_NO, CRE_DT, CRE_USR_ID, UPD_DT, UPD_USR_ID ) VALUES ( #file_no#, #rgst_ofc_cd#, #wh_cd#, #rcv_shp_tp_cd#, #cust_cd#, #splr_rcvr_cd#, #trkr_cd#, #cust_ref_no#, #inter_rmk#, #xter_rmk#, #rcv_shp_flg#, #opr_cd#, #estm_rcv_dt#, #rcv_shp_dt#, #plt_no#, #cntr_no#, #mst_bl_no#, #hus_bl_no#, #cust_ofc_cd#, #splr_rcvr_ofc_cd#, #trkr_ofc_cd#, #opr_ofc_cd#, #len_ut_cd#,
#ctrt_no#, GETDATE() , #cre_usr_id#, GETDATE(), #upd_usr_id# )
/* WhmReceivingItemSQL > createReceivingItemCSQL */

sqlMap.select:
/** WhmReceivingSQL.xml > getNewRcvFileNoRSQL*/
	SELECT WH_RCV_PRFX + CONVERT(VARCHAR(10),ISNULL(MAX(WH_RCV_SEQ_NO),0)+1) AS FILE_NO
  FROM TB_OFC
  WHERE OFC_CD = #rgst_ofc_cd#
  GROUP BY WH_RCV_SEQ_NO, WH_RCV_PRFX
/** WhmReceivingSQL.xml > getMaxLocIdRSQL*/

/** WhmReceivingSQL.xml > getNewRcvFileNoRSQL*/
	SELECT WH_SHP_PRFX + CONVERT(VARCHAR(10),ISNULL(MAX(WH_SHP_SEQ_NO),0)+1) AS FILE_NO
  FROM TB_OFC
  WHERE OFC_CD = #rgst_ofc_cd#
  GROUP BY WH_SHP_SEQ_NO, WH_SHP_PRFX
/** WhmReceivingSQL.xml > getMaxLocIdRSQL*/

/** WhmReceivingSQL.xml > getMaxLocIdRSQL*/
  SELECT 	LOC_ID, LOC_CD
	FROM WHM_LOC
	WHERE 1=1
	AND WH_CD = #wh_cd#
/** WhmReceivingSQL.xml > getMaxLocIdRSQL*/

/* searchItemBeginBalanceRSQL.Query */ SELECT RS.FILE_NO, RS.RGST_OFC_CD, RS.RCV_SHP_FLG, RS.RCV_SHP_DT, ISNULL(RSI.ITM_ENDG_BAL_QTY, 0) ITM_BGN_BAL_QTY FROM WHM_RCV_SHP RS, WHM_RCV_SHP_ITM RSI WHERE 1 =1 AND RS.FILE_NO = RSI.FILE_NO AND RS.RGST_OFC_CD = RSI.RGST_OFC_CD AND RSI.CUST_ITM_ID = #cust_itm_id# AND RS.WH_CD = #wh_cd# AND RS.CTRT_NO = #ctrt_no# AND ISNULL(RS.RCV_SHP_FLG, 'N') = 'Y' AND RS.RCV_SHP_DT = (SELECT MAX(RS.RCV_SHP_DT) FROM WHM_RCV_SHP RS, WHM_RCV_SHP_ITM RSI WHERE 1 =1 AND RS.FILE_NO = RSI.FILE_NO AND RS.RGST_OFC_CD = RSI.RGST_OFC_CD AND RSI.CUST_ITM_ID = #cust_itm_id# AND RS.WH_CD = #wh_cd# AND RS.CTRT_NO = #ctrt_no#
AND RS.FILE_NO != #file_no#
AND ISNULL(RS.RCV_SHP_FLG, 'N') = 'Y' AND RS.RCV_SHP_DT < #rcv_shp_dt# ) ORDER BY RS.RCV_SHP_DT, RS.CRE_DT
